button_1 = Button(
    image=button_image_1,
    borderwidth=0,
    highlightthickness=0,
    command=refresh_data,
    relief="flat"
)
button_1.place(
    x=844.0,
    y=645.0,
    width=376.0,
    height=63.0
)

window.resizable(True, True)

# run the update method that will swap between rivers
refresh_data()
update_river_cards(river_cards)

window.mainloop()
